function ToggleShopItem(n){$.get("/ShoppingLists/ToggleShopItem?product_id="+n,function(){divId="ShopItem-"+n;className="shopItemBought";console.log("Toggle: "+divId);var t=document.getElementById(divId);t.classList.contains(className)?(console.log("Remove: "+divId),t.classList.remove(className)):(console.log("Add: "+divId),t.classList.add(className))})}